UPDATE newroseclaire.composants_recettes_finales 
SET 
	quantite_unite = {{ Table_Ingredients.updatedRow.quantite }}
	WHERE
  id_recette_finale = {{ Table_Ingredients.triggeredRow.id_recette_finale }}
  AND
  (
    (
      {{ Table_Ingredients.triggeredRow.type_composant == "matiere" }} 
      AND id_matiere_premiere = {{ Get_id_matiere_premiere.data[0].id_matiere_premiere }}
    )
    OR
    (
      {{ Table_Ingredients.triggeredRow.type_composant == "sous_recette" }}
      AND id_sous_recette = {{ Get_id_sous_recette.data[0].id_sous_recette}}
    )
  );
